import { useState } from "react";
import { addMonths,format } from 'date-fns';
import Banner from "../../components/Banner"
import data from '../../data.json';


const CATEGORI = ['ALL','K-LEAGUE','KOREA-CUP','CHAMPIONS LEAGUE']



export default function GameSchedule () {
    
const date = new Date(2024,2,1);
const currentYear = format(date,'yyyy');
const YEARS =[currentYear,currentYear-1,currentYear-2,currentYear-3,currentYear-4,currentYear-5];


// 제이슨데이터 중에서 games 프로퍼티의 값을 가져온다.
const games = data.games;
// check box 선택값에 따라서 데이터를 filter 해야하기 때문에 변수명을
// selectedGame 이라고 지었다.
const [selectedGameType, setSelectedGameType] = useState('ALL');
// 불러온 games 의 데이터 구조가 월단위의 큰 객체 => 월별데이터 배열 => 객체
// 구조로 돼있기 때문에 가장 깊숙히 들어있는 값에 따라서 filtering 하기 위해서
// 크게 map() => 그리고 해당 객체의Objectkeys 를 통해 그 => el[Object.keys(el)] 객체에 접근 =>  filter 를 통해서 =>
//  selectedGameType 의 값이 'ALL' 아닐경우에만 조건을 달아서 RETURN 했다.

// 값을 크게 한번 분류 하고 한번더 들어가서 값을 비교해야 하기 때문에 정말 애를 많이 먹었다,
// 어떻게 접근해야 데이터 까지 닿을 수 있을지 한참을 해맸다.
const feed = games.map((el)=> {
    return(
    el[Object.keys(el)].filter((i)=> {
        return selectedGameType === 'ALL' || i.gameType === selectedGameType ;
    })
)})



    return (
        <>
        <Banner aniWidth={'50%'} />
        <section className="gameScheduleArea size1442">
            <div className="categori">
                <div>
                    {CATEGORI.map((el,index)=> < button key={index} onClick={() => setSelectedGameType(el)} className={el === selectedGameType ? 'btn-checkBox active' : 'btn-checkBox'}>{index === 0 && 'ALL'}</button>)}
                </div>
                <ul>
                    <li>
                        2024
                        <ol>
                            <li>2024</li>
                            <li>2023</li>
                            <li>2022</li>
                            <li>2021</li>
                            <li>2020</li>
                        </ol>
                    </li>
                    <li>
                        전체
                        <ol>
                            <li>전체</li>
                            <li>K-LEAGUE</li>
                            <li>KOREA CUP</li>
                            <li>CHAMPIONS LEAGUE</li>
                        </ol>
                    </li>
                </ul>
            </div>
            <Games data={feed} date={date} currentYear={currentYear}  />
        </section>
        </>
    )
}




function Games ({data,currentYear,date}){

    return (
        <>
            {
                data.map((el,index) => {      
                // 해당 달의 경기 일정이 없을때는 엘리먼트를 return 하지 않기 위해서
                // 특정달 el[0] 값이 있을때만 return 하도록 했다.
                    return el[0] && (<div className="games">
                        <p>{index+3}월 {currentYear} {format(addMonths(date,index),'MMMM')}</p>
                        <ol>
                            {el.map((i) => {
                                return(
                                    <li key={i.date}>
                                        <span>
                                            <img src={i.gameSymbol} alt="대회아이콘" />
                                        </span>
                                        <div>
                                            <p>{i.date}</p>
                                            <small>{i.round}</small>
                                            <mark>{i.stadium}</mark>
                                        </div>
                                        <div>
                                            <strong>{i.homeTeam}</strong>
                                            <span><img src={`/images/matchCenter/schedul_icon_${i.homeTeam}.png`} alt="" /></span>
                                            <b>{i.result}</b>
                                            <span><img src={`/images/matchCenter/schedul_icon_${i.awayTeam}.png`} alt="" /></span>
                                            <strong>{i.awayTeam}</strong>
                                        </div>
                                        <ul>
                                            <li><a href="###">리뷰</a></li>
                                            <li><a href="###">VOD</a></li>
                                            <li><a href="###">사진</a></li>
                                        </ul>
                                    </li>)
                                })}
                        </ol>
                    </div>)
                })
            }
        </>
    )
}
