import React, { useRef,useState } from 'react';
import Banner from "../../components/Banner"
import data from "../../data.json"

const cheerSongs = data.cheerSong;

export default function CheerSong(){

const [isActive , setIsActive] = useState(0)

const [isPlaying, setIsPlaying] = useState(false)
const audioRef = useRef(null);
const playMusic = () => {
    if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
};
    return (
        <>
        <Banner aniWidth={"65%"} />
        <section className="cheearSongArea size1442">
            <h2 className="hiddenH2">응원가</h2>
            <ul>{cheerSongs.map((el,index) => {
                return (
                    <li onClick={() => setIsActive(index)} key={index} className={index === isActive && "active"}>
                    <p>{el.title}<button className={isPlaying && isActive === index ? "playing" : ""} onClick={playMusic}></button><audio ref={audioRef} src={el.mp3}></audio></p>
                    <div>{!el.text ? "가사가 없습니다." : el.text.map((i,index)=> { return (i ? <p key={index}>{i}</p> : <br key={index} />)})}<button>응원가 다운받기</button></div>
                    </li>)})}                
            </ul>
        </section>
        </>
    )
}
