import { Link } from 'react-router-dom';
import Banner from './Banner';

// sub page > club Layout

export default function PageBox ({children,onActive,aniWidth,sectionTitle='',Taps}){

    const Tab = Taps;

    return (
        <>
        <Banner aniWidth={aniWidth} onActive={'구단소개'}  />
        <div className="size1442">
            <h2>
                {sectionTitle === '' ? (<>인천,<br />한계를 돌파하라!</>) : sectionTitle }
            </h2>
            <div>
                <aside>
                    {Tab.map((el,index) =>  { return ( <button className={onActive === el.text ? "active" : ''} key={index}>
                        < Link to={el.link}>{el.text}</ Link>
                        </button>)})}
                </aside>
                {children}
            </div>
        </div>
        </>
    )
}
