import { Link } from 'react-router-dom';
import Banner from './Banner';
import data from '../../data.json'

const introductionTabs = data.introductionTabs;

// sub page > club Layout

export default function PageBox ({children,onActive1,aniWidth,sectionTitle=''}){


    return (
        <>
        <Banner aniWidth={aniWidth} onActive1={onActive1}  />
        <div className="size1442">
            <h2>
                {sectionTitle === '' ? (<>인천,<br />한계를 돌파하라!</>) : sectionTitle }
            </h2>
            <div>
                <aside>
                    {introductionTabs.map((el,index) =>  <button className={onActive === el.text ? "active" : ''} key={index}>
                        < Link to={el.link}>{el.text}</ Link>
                        </button>)}
                </aside>
                {children}
            </div>
        </div>
        </>
    )
}
