import { NavLink } from 'react-router-dom';
import Banner from './Banner';
import data from '../../data.json'
import { useLocation } from 'react-router-dom';

const introductionTabs = data.introductionTabs;
const stadiumTabs = data.stadiumTabs;
// sub page > club Layout
export default function PageBox ({children,aniWidth,sectionTitle=''}){
    const location = useLocation();
    const firstlLocation = location.pathname.split('/')[2];
    const secondLocation = location.pathname.split('/')[1];
    console.log(firstlLocation);

    return (
        <>
        <Banner aniWidth={aniWidth} />
        <div className="size1442">
            <h2>
                {sectionTitle === '' ? (<>인천,<br />한계를 돌파하라!</>) : sectionTitle }
            </h2>
            <div>
                <aside>
                    {secondLocation === 'club' ?
                     introductionTabs.map((el,index)=> <button key={index}><NavLink style={({isActive})=>({color: isActive && 'white'})} to={el.link}>{el.text}</NavLink></button>) 
                     :
                     stadiumTabs.map((el,index) => <button key={index}><NavLink style={({isActive})=>({color: isActive && 'white'})} to={el.link}>{el.text}</NavLink></button>)}
                </aside>
                {children}
            </div>
        </div>
        </>
    )

}
