import { useState } from "react"
import Banner from "../../components/Banner"
import data from "../../data.json"
import { el } from "date-fns/locale";

export default function Vod(){

    const [active , setActive] = useState(0);
    const vedios = data.youTubeVideos;
    
    return (
        <>
        <Banner aniWidth={"30%"} />
        <section className="size1442 vodArea">
            <ul>
                {vedios.map((el,index)=> {
                    return (   
                    <li onClick={() => setActive(index)} key={index} className={index === active && "active"}>
                        <p>{el.title}</p>
                        <iframe src={el.src} title={el.title} frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerpolicy="strict-origin-when-cross-origin" allowfullscreen></iframe>
                    </li>
                    )
                })}
            </ul>
        </section>
        </>
    )
}
