import data from '../../../src/data.json'
import { Link } from 'react-router-dom';

// data.json > 'club' tap menu list,


export default function Banner ({onActive01,aniWidth}) {
    
    const clubTaps = data.clubTabs;
    const playerTabs = data.playerTabs;
    
const tabs = (onActive01) => {
    switch (onActive01) {
        case '구단소개':
        case '경기장':
        case '인사말':
            return clubTaps;
        case '코치/지원스태프':
        case '프로':
        case '선수단 일정':
            return playerTabs;
        default:
            return null;
    };
}

    return (
        <div style={{'--ani--width': aniWidth }} className="clubBanner">  
        <ul className="size1442">
            {tabs.map((el,index) => <li className={onActive01 === el.text ? "active" : ""} key={index}><Link to={el.link}>{el.text}</Link></li>)}
        </ul> 
    </div>
    )
}
