import { useState } from "react";
import Banner from "../../components/Banner"
import data from "../../data.json"

const cheerSongs = data.cheerSong;

export default function CheerSong(){
const [isActive , setActive] = useState(Array(cheerSongs.length).fill(false));

const handleClick = (index) => {
    const temp = [...isActive];
    if(temp[index]){
       temp[index] = false;
       return setActive(...temp);
    }
    temp[index] = true;
}

console.log(temp)
    return (
        <>
        <Banner aniWidth={"65%"} />
        <section className="cheearSongArea size1442">
            <h2 className="hiddenH2">응원가</h2>
            <ul>{cheerSongs.map((el,index) => {
                return (
                    <li onClick={handleClick(index)} key={index} className={isActive[index] && "active"}>
                    <p>{el.title}<button></button></p>
                    <div>{!el.text ? "가사가 없습니다." : el.text.map((i)=> { return (i ? <p>{i}</p> : <br />)})}<button>응원가 다운받기</button></div>
                    </li>)})}                
            </ul>
        </section>
        </>
    )
}
