import { useState } from "react";
import { format, addMonths, subMonths, addYears, subYears, getDaysInMonth, startOfMonth, addDays, startOfDay, subDays, endOfDay, endOfMonth } from 'date-fns';
import { ko } from 'date-fns/locale';


export default function Calendar () {
  
  // 1.오늘 날짜를 가져온다.
  const today = new Date(); 
  // 2. 현재 달력에 표시하는 날짜의 기본값을 오늘날짜로 useState 를 사용해서 지정한다.
  const [currentDate , setCurrentDate] = useState(today);
  // 3. 날짜 연도 이동을 위한 click event 함수를 만든다.
  
  // subMonths 함수 : 첫번째 인자에서 두번째 넘겨준 인자만큼 월을 감소시킨다.
  // currenDate : 초기 오늘날짜의 달 에서 -1 
  const handlePrevMonth = () => {
    setCurrentDate(subMonths(currentDate,1));
  }
  // addMonths 함수 : 첫번째 인자에서 두번째 넘겨준 인자만큼 월을 증가시킨다.
  const handleNextMonth = () => {
    setCurrentDate(addMonths(currentDate,1));
  }
  // subYears 함수 : 첫번째 인자에서 두번째 넘겨준 인자만큼 연도를 감소시킨다.
  const handlePrevYear = () =>  {
    setCurrentDate(subYears(currentDate,1));
  }
  // addYears 함수 : 첫번째 인자에서 두번째 넘겨준 인자만큼 연도를 증가시킨다.
  const handleNextYear = () =>  {
    setCurrentDate(addYears(currentDate,1));
  }
  
  // 현재 달력의 시작 날짜정보
  const startDate = startOfMonth(currentDate);
  // 현재 달력의 시작요일 정보
  // 일 6 월 0 화 1 수 2 목 3 금 4 토 5
  const startDayOfWeek = format(startDate, 'i') - 1;
  // 현재달이 몇일 까지 구성돼 있는지
  const daysInMonth = getDaysInMonth(currentDate);
  
  const preMonthDays = startDayOfWeek !== 6 ? [...Array(startDayOfWeek)].map((_,index)=> {
    return subDays(startDate,startDayOfWeek - index);
  }) : [];

  const currentMonthDays = [...Array(daysInMonth)].map((_,index) => {
    return addDays(startDate,index);
  })

  console.log(currentMonthDays)

  const remainingDays = 35 - (preMonthDays.length + currentMonthDays.length);
  const nextMonthDays = [...Array(remainingDays)].map((_,index) => {
    return addDays(endOfMonth(currentDate), index + 1)
  })

  const calendarTiles = [...preMonthDays,...currentMonthDays,...nextMonthDays];

  console.log(calendarTiles);
  return (
    <>
    <div>
      <button onClick={handlePrevYear}>이전연도</button>
      <span>{format(currentDate , 'yyyy' , {locale:ko})}</span>
      <button onClick={handleNextYear}>다음연도</button>
    </div>
    <div>
      <button onClick={handlePrevMonth}>이전 달</button>
      <span>{format(currentDate, 'MM', { locale: ko })}</span>
      <button onClick={handleNextMonth}>다음 달</button>
    </div>
    <div>
      {calendarTiles.map((el)=> {
        return <span>{format(el,'i')}</span>
      })}
    </div>
    </>
  )
}
