import { Link } from 'react-router-dom';
import Banner from './Banner';
import data from '../../data.json'

const introductionTabs = data.introductionTabs;
const stadiumTabs = data.stadiumTabs;
// sub page > club Layout

export default function PageBox ({children,onActive01,onActive02,aniWidth,sectionTitle=''}){

    return (
        <>
        <Banner aniWidth={aniWidth} onActive01={onActive01}  />
        <div className="size1442">
            <h2>
                {sectionTitle === '' ? (<>인천,<br />한계를 돌파하라!</>) : sectionTitle }
            </h2>
            <div>
                <aside>
                    {onActive01 === '구단소개' ?
                     introductionTabs.map((el,index)=> <button key={index} className={onActive02 === el.text ? "active" : ""}><Link to={el.link}>{el.text}</Link></button>) 
                     :
                     stadiumTabs.map((el,index) => <button key={index} className={onActive02 === el.text ? "active" : ""}><Link to={el.link}>{el.text}</Link></button>)}
                </aside>
                {children}
            </div>
        </div>
        </>
    )
}
