import React ,{useState,useRef,useEffect} from "react";
import { useSpring , animated } from "react-spring";


const slides = [
  {id:1, content: 'slide1'},
  {id:2, content: 'slide2'},
  {id:3, content: 'slide3'}
]


  
  const Slider = () => {
    const slides = ['Slide 1', 'Slide 2', 'Slide 3'];
    const [currentIndex, setCurrentIndex] = useState(0);
    const [{ x }, set] = useSpring(() => ({ x: 0 }));

    const sliderRef = useRef();
    const startXRef = useRef(0); // 드래그 시작 위치를 저장
    const isDraggingRef = useRef(false); // 드래그 상태를 저장

    const [slideWidth, setSlideWidth] = useState(0); // 슬라이드의 너비 상태

    // 컴포넌트가 마운트될 때 슬라이드 너비를 계산
    useEffect(() => {
        const handleResize = () => {
            if (sliderRef.current) {
                setSlideWidth(sliderRef.current.clientWidth); // 현재 슬라이더의 너비를 가져와서 상태에 저장
            }
        };

        handleResize(); // 초기 사이즈 계산
        window.addEventListener('resize', handleResize); // 창 크기 변경 시 슬라이드 너비 재계산

        return () => {
            window.removeEventListener('resize', handleResize); // 컴포넌트 언마운트 시 이벤트 리스너 제거
        };
    }, []);

    const handleMouseDown = (e) => {
        isDraggingRef.current = true; // 드래그 시작
        startXRef.current = e.clientX; // 시작 위치 저장
        sliderRef.current.style.cursor = 'grabbing'; // 커서 변경
    };

    const handleMouseMove = (e) => {
        if (!isDraggingRef.current) return; // 드래그 중이 아닐 경우 종료
        const diffX = startXRef.current - e.clientX; // 드래그 거리 계산

        // 슬라이드 위치 업데이트
        set({ x: -currentIndex * slideWidth + (diffX / window.innerWidth) * 100 });
    };

    const handleMouseUp = (e) => {
        isDraggingRef.current = false; // 드래그 종료
        sliderRef.current.style.cursor = 'grab'; // 커서 변경

        const diffX = startXRef.current - e.clientX; // 드래그 거리 계산
        // 한 칸 이동 기준을 슬라이드 너비의 절반으로 설정
        if (diffX > slideWidth / 2 && currentIndex < slides.length - 1) {
            setCurrentIndex(currentIndex + 1); // 오른쪽으로 슬라이드 이동
        } else if (diffX < -slideWidth / 2 && currentIndex > 0) {
            setCurrentIndex(currentIndex - 1); // 왼쪽으로 슬라이드 이동
        }

        // 최종 슬라이드 위치로 이동
        set({ x: -currentIndex * slideWidth });
    };

    const handleMouseLeave = () => {
        isDraggingRef.current = false; // 드래그 종료
        sliderRef.current.style.cursor = 'grab'; // 커서 변경
    };

    return (
        <div
            className="matchSlide-container"
            ref={sliderRef}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            onMouseLeave={handleMouseLeave}
        >
            <animated.div
                className="slide-track"
                style={{
                    transform: x.to((val) => `translateX(${val}%)`),
                }}
            >
                    <div className="slide-item" style={{ width: `${slideWidth}px` }}></div>
                    <div className="slide-item" style={{ width: `${slideWidth}px` }}></div>
                    <div className="slide-item" style={{ width: `${slideWidth}px` }}></div>

            </animated.div>
        </div>
    );
};



export default function LeagueAndMatch() {
 
  return (
    <div className="leagueAndMatch">
      <div className="league-table">
        <table>
          <caption>리그순위</caption>
          <thead>
            <tr>
              <th>순위</th>
              <th>팀</th>
              <th>경기</th>
              <th>승</th>
              <th>무</th>
              <th>패</th>
              <th>승점</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>8</td>
              <td>제주</td>
              <td>31</td>
              <td>12</td>
              <td>2</td>
              <td>17</td>
              <td>38</td>
            </tr>
            <tr>
              <td>9</td>
              <td>대전</td>
              <td>31</td>
              <td>8</td>
              <td>11</td>
              <td>12</td>
              <td>35</td>
            </tr>
            <tr>
              <td>10</td>
              <td>전북</td>
              <td>31</td>
              <td>8</td>
              <td>10</td>
              <td>13</td>
              <td>34</td>
            </tr>
            <tr>
              <td>11</td>
              <td>대구</td>
              <td>31</td>
              <td>8</td>
              <td>10</td>
              <td>13</td>
              <td>34</td>
            </tr>
            <tr>
              <td>12</td>
              <td>인천</td>
              <td>31</td>
              <td>7</td>
              <td>11</td>
              <td>13</td>
              <td>32</td>
            </tr>
          </tbody>
        </table>
      </div> 
      <Slider />
    </div>
  );
}
