import data from '../../../src/data.json'
import { Link } from 'react-router-dom';
import Banner from './Banner';
// data > 'introduction tap menu list 

const introductionTaps = data.introductionTaps;


// sub page > club Layout

export default function PageBox ({children,onActive,aniWidth,sectionTitle=`인천,한계를 돌파하라 !`}){


    return (
        <>
        <Banner aniWidth={aniWidth} onActive={'구단소개'}  />
        <div className="size1442">
            <h2>{sectionTitle}</h2>
            <div>
                <aside>
                    {introductionTaps.map((el,index) => <button className={onActive === el.text ? "active" : ''} key={index}>
                        < Link to={el.link}>{el.text}</ Link>
                        </button>)}
                </aside>
                {children}
            </div>
        </div>
        </>
    )
}
