import { useState } from "react";
import { addMonths,format, set } from 'date-fns';
import Banner from "../../components/Banner"
import data from '../../data.json';


const CATEGORI = ['ALL','K-LEAGUE','KOREA-CUP','CHAMPIONS LEAGUE']

export default function GameSchedule () {
        const games = data.games;
        const [selectedGameType, setSelectedGameType] = useState('ALL');

        function filterMatches (el) {
          setSelectedGameType(el);
          const filteredData = {};   
          Object.keys(games).forEach(month => {
            filteredData[month] = games[month].filter(match => 
              selectedGameType === 'ALL' || match.gameType === selectedGameType
            );
          });      
          return filteredData;
        };

        const feed = filterMatches();
    
    return (
        <>
        <Banner aniWidth={'50%'} />
        <section className="gameScheduleArea size1442">
            <div className="categori">
                <div>
                    {CATEGORI.map((el,index)=> <button onClick={() => setSelectedGameType(el)} className={el === selectedGameType ? 'btn-checkBox active' : 'btn-checkBox'}>{index === 0 && 'ALL'}</button>)}
                </div>
                <ul>
                    <li>
                        2024
                        <ol>
                            <li>2024</li>
                            <li>2023</li>
                            <li>2022</li>
                            <li>2021</li>
                            <li>2020</li>
                        </ol>
                    </li>
                    <li>
                        전체
                        <ol>
                            <li>전체</li>
                            <li>K-LEAGUE</li>
                            <li>KOREA CUP</li>
                            <li>CHAMPIONS LEAGUE</li>
                        </ol>
                    </li>
                </ul>
            </div>
            <Games selectGameType={selectedGameType} />
        </section>
        </>
    )
}




function Games ({feed}){

    const date = new Date(2024,2,1);
    const currentYear = format(date,'yyyy');


    

    return (
        <>
            {
                feed.map((el,index) => {                   
                    
                    return (<div className="games">
                        <p>{index+3}월 {currentYear} {format(addMonths(date,index),'MMMM')}</p>
                        <ol>
                            {el[index+3].map((i,index) => {
                                return(
                                    <li key={index}>
                                        <span>
                                            <img src={i.gameSymbol} alt="대회아이콘" />
                                        </span>
                                        <div>
                                            <p>{i.date}</p>
                                            <small>{i.round}</small>
                                            <mark>{i.stadium}</mark>
                                        </div>
                                        <div>
                                            <strong>{i.homeTeam}</strong>
                                            <span><img src={`/images/matchCenter/schedul_icon_${i.homeTeam}.png`} alt="" /></span>
                                            <b>{i.result}</b>
                                            <span><img src={`/images/matchCenter/schedul_icon_${i.awayTeam}.png`} alt="" /></span>
                                            <strong>{i.awayTeam}</strong>
                                        </div>
                                        <ul>
                                            <li><a href="###">리뷰</a></li>
                                            <li><a href="###">VOD</a></li>
                                            <li><a href="###">사진</a></li>
                                        </ul>
                                    </li>)
                            })}
                        </ol>
                    </div>)
                })
            }
        </>
    )
}
