          <span>월</span>
          <span>화</span>
          <span>수</span>
          <span>목</span>
          <span>금</span>
          <span>토</span>
        </div>
        <div className="날짜상자">
          {이번달데이터.map((el,index) => <span>{el}</span>)}
        </div>
    </div>
  );
};

