import data from '../../../src/data.json'
import { NavLink } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
// data.json > 'club' tap menu list,

const Data = data.bannerCategoris;
const location = useLocation();

const firstLocation = location.pathname.split('/')[1];
const categoris = Data[firstLocation];


export default function Banner ({aniWidth}) {
    

    return (
        <div style={{'--ani--width': aniWidth }} className="banner club">  
            <ul className="size1442">
                {categoris.map((el,index) => <li key={index}><NavLink style={({isActive})=>({color: isActive && 'white'})} to={el.link}>{el.text}</NavLink></li>)}
            </ul> 
        </div>
    )
}
