import { useLocation } from "react-router-dom"




export default function SelectedBox ({props}){

    const Location = useLocation();
    const secondLocation = Location.pathname.split('/')[2]
    
    if(secondLocation === 'ranking'){
        return (
            <>
                <div>
                    <button onClick={()=> props.setSelectedGameType(true)} className={selectedGameType ? 'btn-checkBox active' : 'btn-checkBox'}></button>
                    <button onClick={()=> props.setSelectedGameType(false)} className={!selectedGameType ? 'btn-checkBox active' : 'btn-checkBox'}></button>
                </div>     
                <ul>
                    <li>{props.selectedYear}</li>
                        <ol>
                            {props.YEARS.map((el,index)=> <li key={index} onClick={()=> props.setSelectedYear(el)}>{el}</li>)}                          
                        </ol>
                </ul>
            </>
        )
    }

    return (
        <div>
             <p>
                 {props.selectedYear}
                 <div>
                     {props.YEARS.map((el) => <button onClick={()=> props.setselectedYear(el)} key={el}>{`${el}년`}</button>)}
                 </div>
             </p>
             <p>
                 {props.selectedMonth}
                 <div>
                     {props.selectedMonth !== '전체' && <button onClick={()=> props.setSlectedMonth('전체')}>전체</button>}
                     {props.MONTHS.map((el)=> <button onClick={()=> props.setSlectedMonth(el)} key={el}>{`${el}월`}</button>)}
                 </div>
             </p>
        </div>
    )
}
