const date = new Date();

console.log(date)

const viewYear = date.getFullYear();
const viewMonth = date.getMonth();
console.log(viewYear,viewMonth);


// 지난달의 마지막날의 y,m,date,day...
const prevLast = new Date(viewYear, viewMonth ,0);
// 이번달의 마지막날의 y,m,date,day... date 함수에서 month 값은 0 부터 시작하기 때문에 + 1 값이, 곧 현재 달이다.
const thisLast = new Date(viewYear, viewMonth + 1 ,0);
console.log(prevLast);
console.log(thisLast);

//y,m,date,day...중에서 지난달의 마지막 날의 값을 가져오기 
const plDate = prevLast.getDate();
//y,m,date,day...중에서 지난달의 마지막날의 요일 값을 가져오기 
// 월요일 = 1, 화요일 = 2, 수요일 =3, 목요일 = 4, 금요일 = 5, 토요일 6, 일요일 7
const plDay = prevLast.getDay();

console.log(plDate);
console.log(plDay);
//y,m,date,day...중에서 이번달의 마지막 날의 값을 가져오기
const tlDate  = thisLast.getDate();
//y,m,date,day...중에서 이번달의 마지막날의 요일 값을 가져오기 
const tlDay  = thisLast.getDay();

console.log(tlDate)
console.log(tlDay)


const prevDates = [];
const thisDates = [...Array(tlDate).keys()].slice(1);
const nextDates = [];

if(plDay !== 6){
  for(let i = 0; i < plDay + 1 ; i++){
    prevDates.unshift(plDate - i);
  }
}

for(let i = 1 ; i < 7 - tlDay; i++){
  nextDates.push(i);
}

const dates = prevDates.concat(thisDates , nextDates);
const firstDateIndex = dates.indexOf(1);
const lastDateIndex = dates.lastIndexOf(tlDate);

dates.forEach((el,index) => {
  const condition = index >= firstDateIndex && index < lastDateIndex + 1 
                    ? 'this'
                    : 'other';
        dates[index] = `<div class="date"><span class=${condition}>${el}</span></div>`;
});

document.querySelector('.dates').innerHTML = 'good!';

const today = new Date();
if(viewMonth === today.getMonth() && viewYear === today.getFullYear()){
  for(let el of document.querySelectorAll('.this')){
    if(el.innerHTML === today.getDate()){
      el.classList.add('today');
      break;
    }
  }
}

const prevMonth = () => {
  date.setMonth(date.getMonth() - 1);
};

const nextMonth = () => {
  date.setMonth(date.getMonth() + 1);
};

const goToday = () => {
  date = new Date();
};


export default function Calendar ()  { 

  return (
    <div class="calendar">
        <div class="header">
          <div class="year-month">{viewYear} 년 {viewMonth + 1} 월</div>
          <div class="nav">
            <button class="nav-btn go-prev" onclick={()=>prevMonth()}>&lt;</button>
            <button class="nav-btn go-today" onclick={()=>nextMonth()}>Today</button>
            <button class="nav-btn go-next" onclick={()=>goToday()}>&gt;</button>
          </div>
        </div>
        <div class="main">
          <div class="days">
            <div class="day">일</div>
            <div class="day">월</div>
            <div class="day">화</div>
            <div class="day">수</div>
            <div class="day">목</div>
            <div class="day">금</div>
            <div class="day">토</div>
          </div>
          <div class="dates"></div>
        </div>
    </div>
  );
};
