export default function Calendar ()  { 

  const [오늘날짜,set오늘날짜] = useState(new Date());
  const 이번년 = 오늘날짜.getFullYear();
  const 이번달 = 오늘날짜.getMonth() + 1 ;
  const 지난달 = 이번달 - 1 ;
  const 지난달의마지막날 = new Date(이번년,지난달,0);
  const 지난달의마지막요일값 = 지난달의마지막날.getDay();
  const 달력타일 = [...Array(35)];

  if(지난달의마지막요일값 !==6){
    for(let i = 1 ; i <= 지난달의마지막요일값 + 1 ; i ++){
      달력타일.push('ㅗ');
    }
  }

  const prevMonth = () => {    
    set오늘날짜(오늘날짜.setMonth(오늘날짜.getMonth()))
  }

  const nextMonth = () => {
    set오늘날짜(오늘날짜.setMonth(오늘날짜.getMonth() + 1))
  }


  console.log(오늘날짜,이번년,이번달,지난달,달력타일,지난달의마지막날,지난달의마지막요일값);





  return (
    <div className="달력 size1442">
        <div className="달력헤더">
          <div className="년도월">{이번년} 년 {이번달} 월</div>
          <div className="버튼상자">
            <button onClick={() => prevMonth()} className="nav-btn go-prev" >{ppp}</button>
            <button onClick={() => nextMonth()} className="nav-btn go-next" >{nnn}</button>
          </div>
        </div>
        <div className="요일상자">
          <span>일</span>
          <span>월</span>
          <span>화</span>
          <span>수</span>
          <span>목</span>
          <span>금</span>
          <span>토</span>
        </div>
        <div className="날짜상자">
          {달력타일.map((el,index) => <span>{index}</span>)}
        </div>
    </div>
  );
};

