import React ,{useState,useRef} from "react";
import { useSpring , animated } from "react-spring";


const slides = [
  {id:1, content: 'slide1'},
  {id:2, content: 'slide2'},
  {id:3, content: 'slide3'}
]


function  Slider () {

  // 현재 슬라이드 번호
  const [currentIndex , setCurrentIndex] = useState(0);
  // 현재 드래그 추적하기 위한 참조
  const draggRef = useRef();
  // 드래그 중인지 여부 판단요소
  const [dragging , setDragging] = useState(false);
  // 애니메이션을 위한 스프링 설정
  const [{x},set] = useSpring(()=> ({x:0}));

  // 슬라이드 클릭시 호출 함수
  const handleMouseDown = (e) => {
    // 드래그 상태를 false 에서 true 로 변경
    setDragging(true);
    // 마우스의 위치값을 저장
    draggRef.current = e.clientX;
  }

  //  마우스 이동시 호출될 함수
  const handleMouseMove = (e) => {
    // dragging 의 값이  false 라면 아무동작도 하지 않게 return 
    if(!dragging) return ;
    // 클릭시에 저장해놨던  마우스 의 값에서 이동한 마우스 값의 차이를 계산
    const diff = draggRef.current - e.clientX;
    set({x: -currentIndex * 10 + (diff / window.innerWidth) * 10})
    
  }
  
  const handleMouseUp = (e) => {
    // 드래그중이 아니면 아무런 동작도 실행하지 않게 return
    if(!dragging) return ;
    //  드래그가 끝났으니 setDragg로 false값을 전달
    setDragging(false);
    const diff = draggRef.current - e.clientX;
    
    if (diff >10 && currentIndex < slides.length -1){
    setCurrentIndex(currentIndex + 1 );
  }else if(diff < -10 && currentIndex > 0){
    setCurrentIndex(currentIndex - 1);
  }
  set({x: -currentIndex * 10});
}

return (
  <div 
  className="matchSlide-container"
  onMouseDown={handleMouseDown}
  onMouseMove={handleMouseMove}
  onMouseUp={handleMouseUp}
  onMouseLeave={handleMouseUp}
  >
    <animated.div
    className="slide-track"
    style={{
      transform:x.to ((val) => `translateX(${val}%)`),
      }}>
      <div className="slide-item"></div>
      <div className="slide-item"></div>
      <div className="slide-item"></div>
    </animated.div>  
  </div>
)

}



export default function LeagueAndMatch() {
 
  return (
    <div className="leagueAndMatch">
      <div className="league-table">
        <table>
          <caption>리그순위</caption>
          <thead>
            <tr>
              <th>순위</th>
              <th>팀</th>
              <th>경기</th>
              <th>승</th>
              <th>무</th>
              <th>패</th>
              <th>승점</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>8</td>
              <td>제주</td>
              <td>31</td>
              <td>12</td>
              <td>2</td>
              <td>17</td>
              <td>38</td>
            </tr>
            <tr>
              <td>9</td>
              <td>대전</td>
              <td>31</td>
              <td>8</td>
              <td>11</td>
              <td>12</td>
              <td>35</td>
            </tr>
            <tr>
              <td>10</td>
              <td>전북</td>
              <td>31</td>
              <td>8</td>
              <td>10</td>
              <td>13</td>
              <td>34</td>
            </tr>
            <tr>
              <td>11</td>
              <td>대구</td>
              <td>31</td>
              <td>8</td>
              <td>10</td>
              <td>13</td>
              <td>34</td>
            </tr>
            <tr>
              <td>12</td>
              <td>인천</td>
              <td>31</td>
              <td>7</td>
              <td>11</td>
              <td>13</td>
              <td>32</td>
            </tr>
          </tbody>
        </table>
      </div> 
      <Slider />
    </div>
  );
}
