import data from '../../../src/data.json'
import { Link } from 'react-router-dom';
import styled from "styled-components";
// data.json > 'club' tap menu list,

const clubTaps = data.clubTabs;
const playerTabs = data.playerTabs;

function filterTab (i) {
if(i === '구단소개' || i === '인사말' || i=== '경기장'){
    
    return clubTaps;
} else if( i === '코칭/지원스태프' || i === '프로' || i === '선수단 일정'){
    return playerTabs;
}
}


export default function Banner ({onBannerTab,aniWidth,}) {
    
    const tabs = filterTab(onBannerTab);

    return (
        <div style={{'--ani--width': aniWidth ,'--bg--url' : 'url(../../public/images/club/subBanner_01.png)'}} className="clubBanner">  
        <ul className="size1442">
            {tabs.map((el,index) => <li className={onBannerTab === el.text ? "active" : ""} key={index}><Link to={el.link}>{el.text}</Link></li>)}
        </ul> 
    </div>
    )
}
