import { useState } from 'react';
import { useNavigate , Link } from 'react-router-dom';
export default function Login () {

    const [userId,setUserId] = useState('')
    const [userPassword,setUserPassword] = useState('')

    const navigate = useNavigate();


    const handleSubmit = (e) => {
        e.preventDefault();

        if(userId && userPassword){
            navigate('/')
        }
    }

    return (  
            <section className="loginArea">
                <h2>LOGIN</h2>
                <p>인천유나이티드 홈페이지 방문을 환영합니다!</p>
                <form onSubmit={(e) => handleSubmit(e)}>
                    <label htmlFor="userId">아이디</label>
                    <input value={userId} onChange={(e) => setUserId(e.target.value)} id='userId' type="text" />
                    <label htmlFor="userPassword">비밀번호</label>
                    <input value={userPassword} onChange={(e) => setUserPassword(e.target.value)} id='userPassword' type="password" />
                    <button type="submit">로그인</button>
                </form>
                <p>
                    회원 가입하시면 다양한 부가서비스를 받으실 수 있습니다. <Link to={"joinUs"}>회원가입</Link>
                </p>
                <p>
                    <Link>아이디찾기</Link>|
                    <Link>비밀번호 찾기</Link>
                </p>
            </section>
    )
}
